import styled from "styled-components";

export const ErrorPageContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin: 20px;
`

export const ErrorImage = styled.img`
    width: 64vw;
    max-width: 450px;
`
