import axios from "axios";
 
const useRequestData = (initialData, url) => {

    useEffect(() => {
        axios.get(url, {
            headers: {
                Authorization: localStorage.getItem('token')
            }
        })
            .then((response) => {
            setData(response.data)
            })
            .catch((error) => {
                console.log(error)
                alert('Ocorreu um erro, tente novamente')
